sys_script_fix.script:
function returnPE(user) {
    var gr = new GlideRecord('x_967657_cmaster_certifications');
    gr.addEncodedQuery('certification=MC - Predictive Intelligence^user_name=' + user);
    gr.query();
    if (gr.next()) {
        return true;
    }
}

function returnPA(user) {
    var gr = new GlideRecord('x_967657_cmaster_certifications');
    gr.addEncodedQuery('certification=MC - Performance Analytics^user_name=' + user);
    gr.query();
    if (gr.next()) {
        return true;
    }
}

function returnVA(user) {
    var gr = new GlideRecord('x_967657_cmaster_certifications');
    gr.addEncodedQuery('certification=MC - Virtual Agent^user_name=' + user);
    gr.query();
    if (gr.next()) {
        return true;
    }
}

var gr = new GlideRecord('x_967657_cmaster_certifications');
gr.addEncodedQuery('certificationINCIS - Customer Service Management,CIS - Human Resources,CIS - IT Service Management^certification_status=Current');
gr.query();
while (gr.next()) {
    if (returnVA(gr.user_name) && returnPA(gr.user_name) && returnPE(gr.user_name)) {
        var ga = new GlideRecord('x_967657_cmaster_pro_suite');
        ga.initialize();
        ga.user = gr.user_name.getDisplayValue();
		ga.mainline = gr.certification;
		ga.status = "Ok";
        ga.insert();
    }
	else if  (!returnVA(gr.user_name) || !returnPA(gr.user_name) || !returnPE(gr.user_name)) {
        var ga = new GlideRecord('x_967657_cmaster_pro_suite');
        ga.initialize();
        ga.user = gr.user_name.getDisplayValue();
		ga.mainline = gr.certification;
		ga.status = "Pending";
        ga.insert();
    }
}
